import React from "react";  // Importer React pour utiliser les fonctionnalités de React JS 
import axios from "axios";  // Importer axios pour utiliser les fonctionnalités de axios pour les requêtes HTTP et les requêtes AJAX (API)
import App from "../App";   // Importer App pour utiliser les fonctionnalités de App JS  (reload de la page)
import Avatar from "../Avatar/Avatar"; // Importer Avatar pour utiliser les fonctionnalités de Avatar JS  (avatar)


export default class Member extends React.Component {
  constructor(props) { // Constructeur de la classe Member (Admin).
    super(props); // Appel du constructeur de la classe parente (React.Component).
    this.state = { // Initialisation de l'état membre (Admin).
      isLoading: false, // Initialisation de la variable isLoading (Admin) à false.
      userLogged: props.userLogged,  // Si l'utilisateur est connecté ou non (Admin).
      member: props.member, // Si le membre existe ou non (Admin) 
    };

    this.userUrl = 'http://localhost:8080/api/user'; // Url de l'API user (Admin).
    this.OnSetAdmin = this.OnSetAdmin.bind(this); // On lie la fonction OnSetAdmin au this de la classe Member (Admin) pour pouvoir l'utiliser dans la fonction OnDeleteUser.
    this.OnDeleteUser = this.OnDeleteUser.bind(this); // On lie la fonction OnDeleteUser au this de la classe Member (Admin) et au this de la classe App (reload de la page).
  }

  // Fonction de suppression d'un utilisateur (Admin) (Appelée dans la fonction OnDeleteUser).
  async OnDeleteUser() {
    const { member } = this.state; // Données de l'état membre (Admin).
    if (window.confirm(`Vous êtes sur le point de supprimer définitivement le compte de :\n${member.id} - ${member.firstname} ${member.lastname}\nÊtes vous sûre ?`)) {
      // On lui accorde les droits de suppression de son compte (Admin).
      await axios.delete(this.userUrl + '/' + member.id + '/' + 0, {  // Requête HTTP DELETE (API) pour supprimer un utilisateur (Admin).
        headers: { // En-tête de la requête HTTP (API).
          Authorization: "Bearer " + sessionStorage.getItem("token") // Jeton d'authentification (API) pour accéder à l'API (Admin).
        }
      }).then(() => App.ReloadApp()); // Appel de la fonction ReloadApp de la classe App (reload de la page).
    }
  }




  render() { // Render component Member (Admin).
    const { isLoading, userLogged, member } = this.state; // Données de l'état membre (Admin).
    return (<>
      {!isLoading && userLogged && member ? ( // Si l'utilisateur est connecté et que le membre existe alors on affiche le membre.
        <div className="member" key={'Member-' + member.id}>  {/* Si le membre est admin on affiche le bouton de suppression. */}
          <Avatar key={member.id} dataUser={{ ...member, isProfile: false }} /> {/* Avatar du membre. */}
          <p>{member.firstname} {member.lastname}</p> {/* Nom du membre. */}
          <div>

            {!member.isDelete ? (<>

              {member.id === 1 ? (<>

              </>) : (<>
                {member.isAdmin ? (<> {/* Si le membre est admin on affiche le bouton de suppression. */}
                  <button className="edit actived" aria-label="editAdmin" onClick={this.OnSetAdmin} >
                    <i className="fa-solid fa-shield"></i>
                  </button>
                </>) : (<>

                </>)}
                <button className="delete" aria-label="delUser" onClick={this.OnDeleteUser} >
                  <i className="fa-solid fa-trash"></i>
                </button>
              </>)}

            </>) : (<>
              <button className="delete" aria-label="delUser" onClick={this.OnDeleteUser} >
                <i className="fa-solid fa-trash"></i>
              </button>
            </>)}


          </div>
        </div>
      ) : 'Error to load member!'}
    </>)
  }
}
