import React from "react"; // Importer React depuis le répertoire node_modules
import "./Loader.css" // Importer le fichier CSS depuis le répertoire src (./ = src/)
import Logo from "./../../assets/logoMobile.png"; // Importer l'image du logo depuis le répertoire src (./ = src/) 

// Exporter le composant Loader (export default)
export default class Loader extends React.Component {
  render() {  // Rendu du composant Loader (return)
    return (<>
      <div className='app-loading'>                             {/* Div app-loading */}
        <div className='app-loading-content'>                   {/* Contenu du loader */}
          <img src={Logo} alt='Groupomania-Logo' />             {/* Image du logo */}
          Loading ...                                           {/* Texte du loader */}
        </div>
      </div>
    </>)
  }
}
